-- Cấu trúc bảng cho bảng `tourguides`
--

CREATE TABLE `tourguides` (
  `tourGuide_id` int(11) NOT NULL,
  `tourGuide_name` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tourGuide_email` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tourGuide_phone` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tourGuide_address` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

