CREATE TABLE `tours` (
  `tour_id` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_name` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_price` int(100) NOT NULL,
  `tour_discount` int(100) NOT NULL,
  `tour_image` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_description` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_trip` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_place` varchar(1000) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_vehicle` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_locationStart` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_locationEnd` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_quantytiDate` varchar(100) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `tour_dateStart` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `tour_dateEnd` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `category_id` int(10) UNSIGNED NOT NULL,
  `tourGuide_id` int(10) UNSIGNED NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

